// // // // import React, { useEffect, useState } from 'react';
// // // // import { 
// // // //   FunnelIcon, 
// // // //   Squares2X2Icon, 
// // // //   ListBulletIcon,
// // // //   MagnifyingGlassIcon
// // // // } from '@heroicons/react/24/outline';
// // // // import ProductCard from '../components/ecommerce/ProductCard';
// // // // import { categories } from '../data/products';
// // // // import { productAPI } from '../services/api';
// // // // import { useParams } from 'react-router-dom';

// // // // const Shop = () => {
// // // //   const {userId}=useParams();
// // // //   const [products, setProducts] = useState([]);
// // // //   const [selectedCategory, setSelectedCategory] = useState('all');
// // // //   const [searchQuery, setSearchQuery] = useState('');
// // // //   const [viewMode, setViewMode] = useState('grid');
// // // //   const [sortBy, setSortBy] = useState('featured');



// // // //   useEffect(() => {
// // // //     const fetchProducts = async () => {
// // // //       try {
// // // //         const data = await productAPI.getProducts(userId);
// // // //         setProducts(data.products);
// // // //       } catch (error) {
// // // //         console.error('Failed to fetch products:', error);
// // // //       }
// // // //     };
    
// // // //     fetchProducts();
// // // //   }, [userId]);

// // // //   // Filter products based on category and search
// // // //   const filteredProducts = products.filter(product => {
// // // //     const matchesCategory = selectedCategory === 'all' || product.category === selectedCategory;
// // // //     const matchesSearch = product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
// // // //                          product.description.toLowerCase().includes(searchQuery.toLowerCase());
// // // //     return matchesCategory && matchesSearch;
// // // //   });

// // // //   // Sort products
// // // //   const sortedProducts = [...filteredProducts].sort((a, b) => {
// // // //     switch (sortBy) {
// // // //       case 'price-low':
// // // //         return a.price - b.price;
// // // //       case 'price-high':
// // // //         return b.price - a.price;
// // // //       case 'rating':
// // // //         return b.rating - a.rating;
// // // //       case 'newest':
// // // //         return b.isNew ? 1 : -1;
// // // //       default:
// // // //         return 0;
// // // //     }
// // // //   });

// // // //   return (
// // // //     <div className="page-container">
// // // //       {/* Header */}
// // // //       <div className="mb-8">
// // // //         <h1 className="text-3xl font-bold text-secondary-900 mb-2">Shop Products</h1>
// // // //         <p className="text-secondary-600">Discover quality products and earn commissions with every purchase</p>
// // // //       </div>

