import React, { useState, useEffect } from 'react';
import { CreditCard, ArrowUp, Clock, CheckCircle, X } from 'lucide-react';

const EnhancedWallet = ({ userId, userToken }) => {
  const [wallet, setWallet] = useState({ balance: 0, transactions: [] });
  const [withdrawalRequests, setWithdrawalRequests] = useState([]);
  const [showWithdrawForm, setShowWithdrawForm] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Withdrawal form state
  const [withdrawalForm, setWithdrawalForm] = useState({
    bankName: '',
    accountNumber: '',
    ifscCode: '',
    mobileNumber: '',
    amount: ''
  });

  // Sample data - replace with actual API calls
  useEffect(() => {
    fetchWalletData();
    fetchWithdrawalHistory();
  }, [userId]);

  const fetchWalletData = async () => {
    // Mock API call - replace with actual implementation
    setWallet({
      balance: 2500,
      transactions: [
        { id: 1, date: '2025-01-15', amount: 1000, type: 'Commission', status: 'Completed', description: 'Referral bonus' },
        { id: 2, date: '2025-01-10', amount: 500, type: 'Bonus', status: 'Completed', description: 'Welcome bonus' },
        { id: 3, date: '2025-01-05', amount: -1000, type: 'Withdrawal', status: 'Completed', description: 'Bank transfer' },
        { id: 4, date: '2025-01-01', amount: 1500, type: 'Commission', status: 'Completed', description: 'Monthly commission' },
      ]
    });
  };

  const fetchWithdrawalHistory = async () => {
    // Mock API call - replace with actual implementation
    setWithdrawalRequests([
      {
        id: 1,
        amount: 1000,
        status: 'Completed',
        date: '2025-01-05',
        bankName: 'State Bank of India',
        accountNumber: '****1234',
        ifscCode: 'SBIN0001234'
      },
      {
        id: 2,
        amount: 1500,
        status: 'Pending',
        date: '2025-01-20',
        bankName: 'HDFC Bank',
        accountNumber: '****5678',
        ifscCode: 'HDFC0001234'
      }
    ]);
  };

  const handleWithdrawalSubmit = async () => {
    setLoading(true);
    setError('');
    setSuccess('');

    // Validation
    if (parseFloat(withdrawalForm.amount) < 1000) {
      setError('Minimum withdrawal amount is â‚¹1000');
      setLoading(false);
      return;
    }

    if (parseFloat(withdrawalForm.amount) > wallet.balance) {
      setError('Insufficient balance');
      setLoading(false);
      return;
    }

    try {
      // Mock API call - replace with actual implementation
      const withdrawalData = {
        ...withdrawalForm,
        userId,
        amount: parseFloat(withdrawalForm.amount),
        status: 'Pending',
        date: new Date().toISOString()
      };

      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      setSuccess('Withdrawal request submitted successfully!');
      setShowWithdrawForm(false);
      setWithdrawalForm({
        bankName: '',
        accountNumber: '',
        ifscCode: '',
        mobileNumber: '',
        amount: ''
      });

      // Refresh data
      fetchWithdrawalHistory();
    } catch (err) {
      setError('Failed to submit withdrawal request');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setWithdrawalForm({
      ...withdrawalForm,
      [e.target.name]: e.target.value
    });
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'Pending':
        return <Clock className="h-5 w-5 text-yellow-500" />;
      case 'Completed':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'Rejected':
        return <X className="h-5 w-5 text-red-500" />;
      default:
        return <Clock className="h-5 w-5 text-gray-500" />;
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending':
        return 'text-yellow-600 bg-yellow-50';
      case 'Completed':
        return 'text-green-600 bg-green-50';
      case 'Rejected':
        return 'text-red-600 bg-red-50';
      default:
        return 'text-gray-600 bg-gray-50';
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-6">
        <h2 className="text-3xl font-bold text-gray-900">Wallet Dashboard</h2>
        <p className="text-gray-600">Manage your earnings and withdrawals</p>
      </div>

      {error && (
        <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg">
          <p className="text-red-600">{error}</p>
        </div>
      )}

      {success && (
        <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-lg">
          <p className="text-green-600">{success}</p>
        </div>
      )}

      {/* Balance Card */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-xl shadow-lg p-6 mb-6 text-white">
        <div className="flex justify-between items-center">
          <div>
            <p className="text-blue-100 text-sm">Available Balance</p>
            <p className="text-4xl font-bold">â‚¹{wallet.balance.toLocaleString()}</p>
            <p className="text-blue-100 text-sm mt-2">
              {wallet.balance >= 1000 ? 'Eligible for withdrawal' : `â‚¹${1000 - wallet.balance} more needed for withdrawal`}
            </p>
          </div>
          <div className="flex gap-3">
            <button
              onClick={() => setShowWithdrawForm(true)}
              disabled={wallet.balance < 1000}
              className={`flex items-center gap-2 px-4 py-2 rounded-lg font-medium transition-colors ${
                wallet.balance >= 1000
                  ? 'bg-white text-blue-600 hover:bg-blue-50'
                  : 'bg-gray-400 text-gray-200 cursor-not-allowed'
              }`}
            >
              <ArrowUp className="h-5 w-5" />
              <span>Withdraw</span>
            </button>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Transaction History */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="p-4 border-b border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900">Transaction History</h3>
          </div>
          <div className="max-h-96 overflow-y-auto">
            {wallet.transactions.length === 0 ? (
              <div className="p-8 text-center text-gray-500">
                <CreditCard className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                <p>No transactions yet</p>
              </div>
            ) : (
              <div className="divide-y divide-gray-200">
                {wallet.transactions.map((txn) => (
                  <div key={txn.id} className="p-4 hover:bg-gray-50">
                    <div className="flex justify-between items-center">
                      <div className="flex items-center gap-4">
                        <div className={`p-3 rounded-full ${txn.amount > 0 ? 'bg-green-100' : 'bg-red-100'}`}>
                          <CreditCard className={`h-6 w-6 ${txn.amount > 0 ? 'text-green-600' : 'text-red-600'}`} />
                        </div>
                        <div>
                          <p className="font-medium text-gray-900">{txn.type}</p>
                          <p className="text-sm text-gray-500">{txn.description}</p>
                          <p className="text-xs text-gray-400">{new Date(txn.date).toLocaleDateString()}</p>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className={`font-semibold ${txn.amount > 0 ? 'text-green-600' : 'text-red-600'}`}>
                          {txn.amount > 0 ? '+' : ''}â‚¹{Math.abs(txn.amount).toLocaleString()}
                        </p>
                        <span className={`inline-flex px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(txn.status)}`}>
                          {txn.status}
                        </span>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Withdrawal History */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200">
          <div className="p-4 border-b border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900">Withdrawal History</h3>
          </div>
          <div className="max-h-96 overflow-y-auto">
            {withdrawalRequests.length === 0 ? (
              <div className="p-8 text-center text-gray-500">
                <ArrowUp className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                <p>No withdrawal requests yet</p>
              </div>
            ) : (
              <div className="divide-y divide-gray-200">
                {withdrawalRequests.map((request) => (
                  <div key={request.id} className="p-4 hover:bg-gray-50">
                    <div className="flex justify-between items-start">
                      <div className="flex items-start gap-3">
                        {getStatusIcon(request.status)}
                        <div>
                          <p className="font-medium text-gray-900">â‚¹{request.amount.toLocaleString()}</p>
                          <p className="text-sm text-gray-600">{request.bankName}</p>
                          <p className="text-xs text-gray-500">Account: {request.accountNumber}</p>
                          <p className="text-xs text-gray-500">IFSC: {request.ifscCode}</p>
                          <p className="text-xs text-gray-400">{new Date(request.date).toLocaleDateString()}</p>
                        </div>
                      </div>
                      <span className={`inline-flex px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(request.status)}`}>
                        {request.status}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Withdrawal Form Modal */}
      {showWithdrawForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg shadow-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold text-gray-900">Withdraw Money</h3>
                <button
                  onClick={() => setShowWithdrawForm(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="h-6 w-6" />
                </button>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Bank Name
                  </label>
                  <input
                    type="text"
                    name="bankName"
                    value={withdrawalForm.bankName}
                    onChange={handleInputChange}
                    required
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Enter bank name"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Account Number
                  </label>
                  <input
                    type="text"
                    name="accountNumber"
                    value={withdrawalForm.accountNumber}
                    onChange={handleInputChange}
                    required
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Enter account number"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    IFSC Code
                  </label>
                  <input
                    type="text"
                    name="ifscCode"
                    value={withdrawalForm.ifscCode}
                    onChange={handleInputChange}
                    required
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Enter IFSC code"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Mobile Number
                  </label>
                  <input
                    type="tel"
                    name="mobileNumber"
                    value={withdrawalForm.mobileNumber}
                    onChange={handleInputChange}
                    required
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Enter mobile number"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Amount (â‚¹)
                  </label>
                  <input
                    type="number"
                    name="amount"
                    value={withdrawalForm.amount}
                    onChange={handleInputChange}
                    required
                    min="1000"
                    max={wallet.balance}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    placeholder="Minimum â‚¹1000"
                  />
                  <p className="text-xs text-gray-500 mt-1">
                    Available: â‚¹{wallet.balance.toLocaleString()} | Minimum: â‚¹1,000
                  </p>
                </div>

                <div className="flex gap-3 pt-4">
                  <button
                    type="button"
                    onClick={() => setShowWithdrawForm(false)}
                    className="flex-1 py-3 px-4 border border-gray-300 rounded-lg text-gray-700 font-medium hover:bg-gray-50"
                  >
                    Cancel
                  </button>
                  <button
                    type="button"
                    onClick={handleWithdrawalSubmit}
                    disabled={loading}
                    className="flex-1 py-3 px-4 bg-blue-600 text-white rounded-lg font-medium hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {loading ? 'Processing...' : 'Submit Request'}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EnhancedWallet;
