import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import {
  TruckIcon,
  CheckCircleIcon,
  XCircleIcon,
  ClockIcon,
  ArrowPathIcon,
  QrCodeIcon,
  DevicePhoneMobileIcon
} from '@heroicons/react/24/outline';
import { orderAPI } from '../../services/api';

const Orders = () => {
  const { userId } = useParams();
  const [activeTab, setActiveTab] = useState('all');
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await orderAPI.getOrders(userId);

        // Ensure response.data exists and is an array
        if (response && Array.isArray(response.data)) {
          setOrders(response.data);
        } else {
          setOrders([]);
          setError('Received invalid orders data format');
        }
      } catch (err) {
        console.error('Error fetching orders:', err);
        setError('Failed to load orders. Please try again later.');
        setOrders([]); // Reset to empty array on error
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, [userId]);

  // Safely filter orders based on active tab
  const filteredOrders = activeTab === 'all' 
    ? orders || []
    : (orders || []).filter(order => order?.status === activeTab);

  const getStatusIcon = (status) => {
    switch (status) {
      case 'delivered':
        return <CheckCircleIcon className="h-5 w-5 text-green-500" />;
      case 'shipped':
        return <TruckIcon className="h-5 w-5 text-blue-500" />;
      case 'processing':
        return <ClockIcon className="h-5 w-5 text-yellow-500" />;
      case 'cancelled':
        return <XCircleIcon className="h-5 w-5 text-red-500" />;
      default:
        return <ClockIcon className="h-5 w-5 text-gray-500" />;
    }
  };

  const getPaymentStatusIcon = (status) => {
    if (!status) return <ClockIcon className="h-5 w-5 text-gray-500" />;

    switch (status) {
      case 'Done':
        return <CheckCircleIcon className="h-5 w-5 text-green-500" />;
      case 'Pending':
        return <ClockIcon className="h-5 w-5 text-yellow-500" />;
      default:
        return <ClockIcon className="h-5 w-5 text-gray-500" />;
    }
  };

  const getStatusText = (status) => {
    if (!status) return 'Processing';

    switch (status) {
      case 'delivered':
        return 'Delivered';
      case 'shipped':
        return 'Shipped';
      case 'processing':
        return 'Processing';
      case 'cancelled':
        return 'Cancelled';
      default:
        return 'Processing';
    }
  };

  const getPaymentStatusText = (status) => {
    if (!status) return 'Payment Pending';

    switch (status) {
      case 'Done':
        return 'Payment Successful';
      case 'Pending':
        return 'Payment Pending';
      default:
        return 'Payment Pending';
    }
  };

  // Safely format date
  const formatDate = (dateString) => {
    try {
      return dateString ? new Date(dateString).toLocaleDateString() : 'N/A';
    } catch {
      return 'N/A';
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <h1 className="text-2xl font-bold text-gray-900 mb-6">My Orders</h1>

      {/* Order Status Tabs */}
      <div className="border-b border-gray-200 mb-6">
        <nav className="-mb-px flex space-x-8">
          <button
            onClick={() => setActiveTab('all')}
            className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'all' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
          >
            All Orders
          </button>
          <button
            onClick={() => setActiveTab('processing')}
            className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'processing' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
          >
            Processing
          </button>
          <button
            onClick={() => setActiveTab('shipped')}
            className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'shipped' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
          >
            Shipped
          </button>
          <button
            onClick={() => setActiveTab('delivered')}
            className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'delivered' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
          >
            Delivered
          </button>
          <button
            onClick={() => setActiveTab('cancelled')}
            className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${activeTab === 'cancelled' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'}`}
          >
            Cancelled
          </button>
        </nav>
      </div>

      {/* Orders List */}
      {loading ? (
        <div className="text-center py-12 bg-white rounded-lg shadow">
          <p className="text-gray-500">Loading orders...</p>
        </div>
      ) : error ? (
        <div className="text-center py-12 bg-white rounded-lg shadow">
          <p className="text-red-500">{error}</p>
        </div>
      ) : (filteredOrders && filteredOrders.length > 0) ? (
        <div className="bg-white shadow overflow-hidden sm:rounded-md">
          <ul className="divide-y divide-gray-200">
            {filteredOrders.map((order) => (
              <li key={order?.id || Math.random()}>
                <div className="px-4 py-4 sm:px-6">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center">
                      {getStatusIcon(order?.status)}
                      <p className="ml-2 text-sm font-medium text-gray-900">
                        Order #{order?.id || 'N/A'}
                      </p>
                    </div>
                    <div className="ml-2 flex-shrink-0 flex space-x-2">
                      <p className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        order?.status === 'delivered' ? 'bg-green-100 text-green-800' :
                        order?.status === 'shipped' ? 'bg-blue-100 text-blue-800' :
                        order?.status === 'processing' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-red-100 text-red-800'
                      }`}>
                        {getStatusText(order?.status)}
                      </p>
                      <p className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                        order?.paymentStatus === 'Done' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {getPaymentStatusText(order?.paymentStatus)}
                      </p>
                    </div>
                  </div>
                  <div className="mt-2 sm:flex sm:justify-between">
                    <div className="sm:flex">
                      <p className="flex items-center text-sm text-gray-500">
                        Ordered on {formatDate(order?.date)}
                      </p>
                    </div>
                    <div className="mt-2 flex items-center text-sm text-gray-500 sm:mt-0">
                      <p className="font-medium">Total: ${order?.total?.toFixed(2) || '0.00'}</p>
                    </div>
                  </div>

                  {/* Order Items */}
                  {order?.items && (
                    <div className="mt-4 border-t border-gray-200 pt-4">
                      <h4 className="text-sm font-medium text-gray-900 mb-2">Items</h4>
                      <ul className="divide-y divide-gray-200">
                        {order.items.map((item, itemIdx) => (
                          <li key={itemIdx} className="py-2">
                            <div className="flex justify-between">
                              <div>
                                <p className="text-sm font-medium text-gray-900">{item?.name || 'Unknown Item'}</p>
                                {item?.mlmPoints > 0 && (
                                  <p className="text-xs text-blue-600">MLM Package - {item.mlmPoints} Points</p>
                                )}
                              </div>
                              <div className="text-right">
                                <p className="text-sm text-gray-900">
                                  ${item?.price?.toFixed(2) || '0.00'} x {item?.quantity || 1}
                                </p>
                                <p className="text-sm text-gray-500">
                                  ${((item?.price || 0) * (item?.quantity || 1)).toFixed(2)}
                                </p>
                              </div>
                            </div>
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}

                  {/* Payment Method */}
                  <div className="mt-4 border-t border-gray-200 pt-4">
                    <h4 className="text-sm font-medium text-gray-900 mb-2">Payment Information</h4>
                    <div className="flex items-center">
                      {order?.payment?.method === 'qr' ? (
                        <>
                          <QrCodeIcon className="h-5 w-5 text-gray-500 mr-2" />
                          <p className="text-sm text-gray-700">Paid via QR Code</p>
                        </>
                      ) : order?.payment?.method === 'upi' ? (
                        <>
                          <DevicePhoneMobileIcon className="h-5 w-5 text-gray-500 mr-2" />
                          <p className="text-sm text-gray-700">Paid via UPI {order?.payment?.upiId && `(${order.payment.upiId})`}</p>
                        </>
                      ) : (
                        <p className="text-sm text-gray-700">
                          Payment Method: {order?.payment?.method || 'Not specified'}
                        </p>
                      )}
                    </div>
                    <div className="flex items-center mt-1">
                      <p className="text-sm text-gray-500 mr-1">Payment Status:</p>
                      {getPaymentStatusIcon(order?.paymentStatus)}
                      <span className={`ml-1 font-medium ${order?.paymentStatus === 'Done' ? 'text-green-600' : 'text-yellow-600'}`}>
                        {order?.paymentStatus || 'Pending'}
                      </span>
                    </div>
                  </div>

                  {/* MLM Points Earned */}
                  {order?.mlmPointsEarned > 0 && order?.status !== 'cancelled' && (
                    <div className="mt-4 bg-blue-50 border border-blue-200 rounded-lg p-3">
                      <div className="flex items-center">
                        <ArrowPathIcon className="h-5 w-5 text-blue-600 mr-2" />
                        <div>
                          <h4 className="text-sm font-medium text-blue-800">MLM Points Earned</h4>
                          <p className="text-sm text-blue-700">{order.mlmPointsEarned} points added to your account</p>
                        </div>
                      </div>
                    </div>
                  )}

                  {/* Order Actions */}
                  <div className="mt-4 flex justify-end space-x-3">
                    {order?.id && (
                      <Link
                        to={`/orders/${order.id}`}
                        className="inline-flex items-center px-3 py-2 border border-gray-300 shadow-sm text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                      >
                        View Details
                      </Link>
                    )}
                    {order?.status === 'delivered' && (
                      <button
                        type="button"
                        className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                      >
                        Buy Again
                      </button>
                    )}
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div className="text-center py-12 bg-white rounded-lg shadow">
          <h3 className="text-lg font-medium text-gray-900">No orders found</h3>
          <p className="mt-1 text-sm text-gray-500">
            {activeTab === 'all' 
              ? "You haven't placed any orders yet."
              : `You don't have any ${activeTab} orders.`}
          </p>
          <div className="mt-6">
            <Link
              to="/shop"
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700"
            >
              Continue Shopping
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default Orders;
