// export default SignupForm;



import React, { useState, useEffect } from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";
import { Link, useNavigate } from "react-router-dom";
import { auth, RecaptchaVerifier, signInWithPhoneNumber } from "../../firebase";
import { authAPI } from "../../services/api";

const SignupForm = () => {
  const [step, setStep] = useState(1); // 1 = form, 2 = OTP, 3 = success
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [mobileForOtp, setMobileForOtp] = useState("");
  const [otp, setOtp] = useState("");
  const [formData, setFormData] = useState(null);
  const navigate = useNavigate();

  // ✅ Validation schema
  const validationSchema = Yup.object({
    name: Yup.string().min(2).max(50).required("Name is required"),
    email: Yup.string().email("Invalid email").nullable(),
    mobileNumber: Yup.string()
      .matches(/^[0-9]{10}$/, "Enter valid 10-digit mobile number")
      .required("Mobile number is required"),
    password: Yup.string()
      .min(6)
      .matches(/^(?=.*[a-zA-Z])(?=.*\d).+$/, "At least one letter & one number")
      .required("Password is required"),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref("password"), null], "Passwords must match")
      .required("Confirm password is required"),
  });

  // ✅ Setup reCAPTCHA
  const setupRecaptcha = () => {
    if (!window.recaptchaVerifier) {
      window.recaptchaVerifier = new RecaptchaVerifier(
        "recaptcha-container",
        {
          size: "invisible", // can be 'normal' if you want visible recaptcha
          callback: (response) => {
            console.log("Recaptcha solved:", response);
          },
        },
        auth
      );
    }
  };

  // ✅ Send OTP
  const sendOtp = async (mobile) => {
    setLoading(true);
    setError("");
    setMessage("");

    try {
      setupRecaptcha();
      const phoneNumber = `+91${mobile}`;
      const appVerifier = window.recaptchaVerifier;

      const confirmationResult = await signInWithPhoneNumber(
        auth,
        phoneNumber,
        appVerifier
      );

      window.confirmationResult = confirmationResult;
      setMessage("OTP sent successfully ✅");
      setMobileForOtp(mobile);
      setStep(2);
    } catch (err) {
      console.error("OTP Error:", err);
      setError(err.message || "Failed to send OTP.");
    } finally {
      setLoading(false);
    }
  };

  // ✅ Verify OTP + complete signup
  const verifyOtp = async () => {
    setLoading(true);
    setError("");

    try {
      await window.confirmationResult.confirm(otp);

      const response = await authAPI.completeSignup({
        ...formData,
        mobileNumber: mobileForOtp,
      });

      if (response.success) {
        setMessage(
          `Signup successful ✅ Your User ID: ${response.data.userId}`
        );
        console.log("All User IDs for this number:", response.data.allUserIds);
        setStep(3);
      } else {
        throw new Error(response.message || "Signup failed");
      }
    } catch (err) {
      console.error("Signup OTP error:", err);
      setError(err.message || "Invalid OTP. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  // ✅ Handle signup form submit
  const handleFormSubmit = async (values) => {
    setFormData(values);
    await sendOtp(values.mobileNumber);
  };

  // ================= Screens =================

  // Step 3: Success
  if (step === 3) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-6">
        <div className="max-w-md w-full space-y-6 text-center">
          <h2 className="text-2xl font-bold text-green-600">
            Registration Complete 🎉
          </h2>
          <p className="text-gray-600">Proceed to login with your credentials.</p>
          <button
            onClick={() => navigate("/login")}
            className="w-full py-2 px-4 rounded-md bg-indigo-600 text-white hover:bg-indigo-700"
          >
            Go to Login
          </button>
        </div>
      </div>
    );
  }

  // Step 2: OTP Verification
  if (step === 2) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-6">
        <div id="recaptcha-container"></div>
        <div className="max-w-md w-full space-y-6 text-center">
          <h2 className="text-xl font-bold">Verify OTP</h2>
          <p className="text-gray-600">Enter the OTP sent to +91 {mobileForOtp}</p>

          {message && <p className="text-green-600">{message}</p>}
          {error && <p className="text-red-600">{error}</p>}

          <input
            type="text"
            value={otp}
            onChange={(e) => setOtp(e.target.value.replace(/\D/g, "").slice(0, 6))}
            placeholder="Enter 6-digit OTP"
            className="w-full px-3 py-2 border rounded-md text-center"
          />

          <button
            onClick={verifyOtp}
            disabled={loading || otp.length !== 6}
            className="w-full py-2 mt-2 bg-indigo-600 text-white rounded-md"
          >
            {loading ? "Verifying..." : "Verify OTP"}
          </button>

          <button
            onClick={() => sendOtp(mobileForOtp)}
            disabled={loading}
            className="w-full py-2 mt-2 bg-gray-100 text-gray-700 rounded-md"
          >
            Resend OTP
          </button>
        </div>
      </div>
    );
  }

  // Step 1: Registration Form
  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-6">
      <div id="recaptcha-container"></div>
      <div className="max-w-md w-full space-y-6">
        <h2 className="text-2xl font-bold text-center">Create your account</h2>

        <Formik
          initialValues={{
            name: "",
            email: "",
            mobileNumber: "",
            password: "",
            confirmPassword: "",
          }}
          validationSchema={validationSchema}
          onSubmit={handleFormSubmit}
        >
          {({ isValid, dirty }) => (
            <Form className="space-y-4">
              {/* Name */}
              <div>
                <label className="block text-sm font-medium">Full Name</label>
                <Field
                  name="name"
                  type="text"
                  className="mt-1 w-full px-3 py-2 border rounded-md"
                />
                <ErrorMessage
                  name="name"
                  component="div"
                  className="text-sm text-red-600"
                />
              </div>

              {/* Email */}
              <div>
                <label className="block text-sm font-medium">Email (optional)</label>
                <Field
                  name="email"
                  type="email"
                  className="mt-1 w-full px-3 py-2 border rounded-md"
                />
                <ErrorMessage
                  name="email"
                  component="div"
                  className="text-sm text-red-600"
                />
              </div>

              {/* Mobile */}
              <div>
                <label className="block text-sm font-medium">Mobile Number</label>
                <div className="flex mt-1">
                  <span className="px-3 py-2 border bg-gray-50 text-gray-500">
                    +91
                  </span>
                  <Field
                    name="mobileNumber"
                    type="tel"
                    maxLength="10"
                    placeholder="9876543210"
                    className="flex-1 px-3 py-2 border rounded-r-md"
                  />
                </div>
                <ErrorMessage
                  name="mobileNumber"
                  component="div"
                  className="text-sm text-red-600"
                />
              </div>

              {/* Password */}
              <div>
                <label className="block text-sm font-medium">Password</label>
                <Field
                  name="password"
                  type="password"
                  className="mt-1 w-full px-3 py-2 border rounded-md"
                />
                <ErrorMessage
                  name="password"
                  component="div"
                  className="text-sm text-red-600"
                />
              </div>

              {/* Confirm Password */}
              <div>
                <label className="block text-sm font-medium">Confirm Password</label>
                <Field
                  name="confirmPassword"
                  type="password"
                  className="mt-1 w-full px-3 py-2 border rounded-md"
                />
                <ErrorMessage
                  name="confirmPassword"
                  component="div"
                  className="text-sm text-red-600"
                />
              </div>

              {error && (
                <div className="bg-red-50 p-3 text-sm text-red-700 rounded-md">
                  {error}
                </div>
              )}

              <button
                type="submit"
                disabled={loading || !isValid || !dirty}
                className="w-full py-2 px-4 rounded-md bg-indigo-600 text-white disabled:opacity-50"
              >
                {loading ? "Sending OTP..." : "Sign Up"}
              </button>
            </Form>
          )}
        </Formik>
      </div>
    </div>
  );
};

export default SignupForm;
